    private void checkoutActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_checkoutActionPerformed
        // TODO add your handling code here:
        
        if(ceklempuyangan.isSelected()){
        //memunculkan hasil pembelian tiket lempuyangan
        int JumlahBeliL=Integer.parseInt(jumlahL.getText());
        int HargaTiketL=Integer.parseInt(hargaL.getText());
        int tot1=HargaTiketL*JumlahBeliL;
        hasil_checkout.setText("Rp "+tot1);  
        }
        if(cektugu.isSelected()){
        //memunculkan hasil pembelian tiket tugu
        int JumlahBeliT=Integer.parseInt(jumlahT.getText());
        int HargaTiketT=Integer.parseInt(hargaT.getText());
        int tot2=HargaTiketT*JumlahBeliT;
        hasil_checkout.setText("Rp "+tot2);
        }
        if(cekmaguwo.isSelected()){
        //memunculkan hasil pembelian tiket maguwo
        int JumlahBeliM=Integer.parseInt(jumlahM.getText());
        int HargaTiketM=Integer.parseInt(hargaM.getText());
        int tot3=HargaTiketM*JumlahBeliM;
        hasil_checkout.setText("Rp "+tot3);
        }
        if(ceksentolo.isSelected()){
        //memunculkan hasil pembelian tiket sentolo
        int JumlahBeliS=Integer.parseInt(jumlahS.getText());
        int HargaTiketS=Integer.parseInt(hargaS.getText());
        int tot4=HargaTiketS*JumlahBeliS;
        hasil_checkout.setText("Rp "+tot4);
        }   
        
        // jika metode tunai dipilih maka pada via akan tampil metode pembayaran yang dipilih
        if(tunai.isSelected())
            via.setText(tunai.getActionCommand());
        if(mandiri.isSelected())
            via.setText(mandiri.getActionCommand());
        if(bca.isSelected())
            via.setText(bca.getActionCommand());
        if(bri.isSelected())
            via.setText(bri.getActionCommand());
        if(dana.isSelected())
            via.setText(dana.getActionCommand());
        if(bni.isSelected())
            via.setText(bni.getActionCommand());
        
    }//GEN-LAST:event_checkoutActionPerformed

