    private void lempuyanganActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_lempuyanganActionPerformed
        // TODO add your handling code here:
        // setting harga dari tiap kereta
        String nama_keretaL=(String)lempuyangan.getSelectedItem();
        switch (nama_keretaL){
            case "Jaka Tingkir":
                hargaL.setText("50000");
                break;
            case "Fajar Utama":
                hargaL.setText("100000");
                break;
            case "Senja Utama":
                hargaL.setText("80000");
                break; 
            case "KRL":
                hargaL.setText("10000");
                break;
        }
    }//GEN-LAST:event_lempuyanganActionPerformed

